<?php 

function sendNotif($to, $notif){
  $apiKey = "AAAA7AhR99g:APA91bG189OsUI34-X8dzT326MP5ZS3zXVq12FxqkPsQ2KKtBY1zC01xbJm5ceTpOV22nGlKQknk8jDdmXZs2zYOgAZpaeIMiTjLlATaTvp4zfBoP80-u6TK-y6VWwEOWW3WkO8EeYCa";

  // Generated by curl-to-PHP: http://incarnate.github.io/curl-to-php/
  $ch = curl_init();

  $url = "https://fcm.googleapis.com/fcm/send";
  $fields = json_encode(array(
                              'to' => $to, 
                              'notification' => $notif
                              )); 

  curl_setopt($ch, CURLOPT_URL, $url);
  curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
  curl_setopt($ch, CURLOPT_POST, 1);
  curl_setopt($ch, CURLOPT_POSTFIELDS,($fields));

  $headers = array();
  $headers[] = 'Authorization: key ='.$apiKey;
  $headers[] = 'Content-Type: application/json';
  curl_setopt($ch, CURLOPT_HTTPHEADER, $headers);

  $result = curl_exec($ch);
  if (curl_errno($ch)) {
      echo 'Error:' . curl_error($ch);
  }
  curl_close($ch);
}

$to = '/topics/PENGUMUMAN';
$notif = array(
  'title' => 'Notif From PHP Native, Tipe : Broadcast Message',
  'body' => 'Notif From PHP Native, Tipe : Broadcast Message'
);

sendNotif($to, $notif);

